create table forms
(
    id          integer               not null
        constraint forms_submitted_pkey
            primary key,
    type        varchar               not null
        constraint formtype_fk
            references "formMETA",
    email       varchar               not null
        constraint email_fk
            references users,
    status      varchar               not null,
    data        text                  not null,
    jao         boolean default false not null,
    ao          boolean default false not null,
    ar          boolean default false not null,
    message     text,
    budget      text,
    hod         boolean default false,
    submit_date timestamp,
    department  varchar,
    hod_com     boolean default false,
    committee   integer default 0
);

alter table forms
    owner to postgres;

